import React from 'react';
import ReactDOM from 'react-dom';
import Table from 'rc-table';
import Animate from 'rc-animate';
import 'rc-table/assets/index.css';
import 'rc-table/assets/animation.css';

const AnimateBody = props => <Animate transitionName="move" component="tbody" {...props} />;

class cart extends React.Component {
  constructor(props) {
    super(props);
    this.columns = [
      { title: 'Title', dataIndex: 'a', key: 'a', width: 500 },
      { id: '123', title: 'QTY', dataIndex: 'b', key: 'b', width: 100 },
      { title: 'Price', dataIndex: 'c', key: 'c', width: 200 },
      {
        title: 'Operations',
        dataIndex: '',
        key: 'd',
        render: (text, record) => (
          <a onClick={e => this.onDelete(record.key, e)} href="#">
            Delete
          </a>
        ),
      },
    ];
    this.state = {
      data: [
        { a: 'Belted Plaid Blazer', b: '1', c: 'Ksh 750', key: '2' },
        { a: 'Black Slim Jeans',  b: '1',c: 'Ksh 450', key: '3' },
      ],
    };
  }

  onDelete(key, e) {
    console.log('Delete', key);
    e.preventDefault();
    const data = this.state.data.filter(item => item.key !== key);
    this.setState({ data });
  }

  onAdd() {
    const data = [...this.state.data];
    data.push({
      a: 'new data',
      b: 'new data',
      c: 'new data',
      key: Date.now(),
    });
    this.setState({ data });
  }

  render() {
    return (
      <div style={{ margin: 20 }}>
        <h2>Checkout</h2>
        <button onClick={() => this.onAdd()}>添加</button>
        <Table
          columns={this.columns}
          data={this.state.data}
          components={{
            body: { wrapper: AnimateBody },
          }}
        />
          <MDBBtn color="white" size="sm" style={{boxShadow:" 0 2px 0px 0 rgba(0, 0, 0, 0.16), 0 2px 2px 0 rgba(0, 0, 0, 0.12)",marginRight: "27%",}}>
          Add to bag
          </MDBBtn>
      </div>
    );
  }
}

export default cart;
