import * as React from 'react';
import Masonry from 'react-masonry-component';
import { MDBCard, MDBCardBody, MDBCardImage,MDBCardTitle,MDBCardText,MDBBtn,MDBCol} from "mdbreact";

const murl="http://lmgexhibitions.kilaqitu.com/img/";
const masonryOptions = {
    transitionDuration: 0
};

const imagesLoadedOptions = { background: '.my-bg-image-el' }
const theData = [
  {
      img: murl+"p1.jpeg",
      txt: 'Kenya Homes Expo 2018, KICC'
  },
  { 
    img: murl+"p2.jpeg",
    txt: 'India Kenya Trade Expo 2018, KICC'
},
{ img: murl+"p3.jpeg",
  txt: 'Afriwood Kenya2019,KICC'
},
{img: murl+"p4.jpeg",
  txt: 'Kenya International Trade Show 2019,KICC'
},
{img: murl+"p5.jpeg",
  txt: 'Magical Kenya Travel Expo 2017, KICC'
},
{img: murl+"p6.jpeg",
  txt: 'Indo-Africa B2B 2017,Kenya KICC'
},
{
  img: murl+"p7.jpeg",
  txt: 'Build Expo Africa ,Kenya 2018, KICC',
  t1:"22nd Buildexpo Africa 2019 is the only show with the widest range of the latest technology in building material, mining machines, construction machinery and heavy equipment."
},
{
  img: murl+"p8.jpeg",
  txt: 'Big 5 Construct Kenya 2019, KICC'
}
]
class Services extends React.Component {
    render() {
        const childElements = theData.map((e,i)=>{
           return (
                // <li className="image-element-class">
                //   {e.txt}
                //     <img src={e.img}/>
                // </li>
                
      <MDBCard style={{ width:"92%",maxWidth: "400px",marginRight: "12px",marginLeft: "12px",marginTop: "32px",}}>
 
        <MDBCardImage className="img-fluid" src={e.img} waves />
        <MDBCardBody>
          <MDBCardTitle>{e.txt}</MDBCardTitle>
          <MDBCardText>
            Some quick example text to build on the card title and make
            up the bulk of the card&apos;s content.
          </MDBCardText>
        </MDBCardBody>
      </MDBCard>
                    
            );
        });
    
        return (

      <Masonry
                className={'my-gallery-class'} // default ''
                elementType={'div'} // default 'div'
                options={masonryOptions} // default {}
                disableImagesLoaded={false} // default false
                updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
                imagesLoadedOptions={imagesLoadedOptions} // default {}
                
                style={{maxWidth:"1000px",marginLeft:"auto",marginRight:"auto",marginTop:"72px", }} 
            >
                {childElements}
                <br/>
        </Masonry>

        
        );
    }
}
export default Services;
