import React from "react";
import { Form, InputNumber,Input, Button,Icon,Tooltip,Breadcrumb } from 'antd';

import { MDBCard, MDBCardBody, MDBCol, MDBRow,MDBIcon} from "mdbreact";
import { Redirect } from 'react-router';
import {Link } from "react-router-dom";
import TextArea from "antd/lib/input/TextArea";

// const { MonthPicker, RangePicker } = DatePicker;
const axios = require('axios');
const ori="vertical";
const mButton = {
  display: "block",
  margin: "72px auto 0",
  background:" rgb(3, 67, 101)",
  background: "-moz-linear-gradient(315deg, #3f0d12 0%, #a71d31 74%)",
  background: "linear-gradient(315deg, #3f0d12 0%, #a71d31 74%)",
  boxShadow: "1px 5px 5px #260303",
  border: "none",
  transition:" background .45s @ease-out",
  width: "132px",
  lineHeight: "42px",
  height: "42px",
  borderRadius: "42px",


};
class Shop1 extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
      redirect: false,
      dbMessage:""
    };
  
   
    handleSubmit = e => {
      e.preventDefault();
  
      this.props.form.validateFields((err, fieldsValue) => {
        if (err) {
          return;
        }
        const values = {
          fieldsValue
        };
        console.log('Received values of form: ', values);
        axios({
          method: 'post',
          // url: 'http://localhost/nw/vap/regApi.php?apicall=signup'
          url: 'http://localhost/nw/kq/api1.php?apicall=signup',
          data: values,
          config: { headers: {'Content-Type': 'multipart/form-data' }}
      })
      .then((response) => {

        if(response.data.error){
          console.log("error "+response.data.message);
          this.setState({
            dbMessage: response.data.message
         });
          }else if(response.data.message=="Inserted"){
            this.setState({
              redirect: true, 
              dbMessage: response.data.message
           });

          }else{
            this.setState({
              dbMessage: response.data.message
           });
          var result=response.data.message;
          JSON.stringify(result)
          console.log("success "+JSON.stringify(result));
          }

      })
      .catch(function (response) {
          //handle error
          console.log("error"+response)
      });
      });
    };

  
  
    compareToFirstPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && value !== form.getFieldValue('password')) {
        callback('Two passwords that you enter is inconsistent!');
      } else {
        callback();
      }
    };
  
    validateToNextPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && this.state.confirmDirty) {
        form.validateFields(['confirm'], { force: true });
      }
      callback();
    };
    
render() {
  
  //const { getFieldDecorator } = this.props.form;
  const { getFieldDecorator,} = this.props.form;

  const { redirect  } = this.state;
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 8 },
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 16 },
    },
  };
 
  if (redirect) {
    return <Redirect to='/geo'/>;
  }
  return (
<div style={{maxWidth:"800px",marginLeft:"auto",marginRight:"auto",paddingTop:"72px"}}>



<MDBCard color="white" text="black" className="text-center">
  <MDBCardBody>
  <MDBCard color="white" text="black" className="text-center" >
      <MDBCardBody>
      <Breadcrumb>
      <Breadcrumb.Item >
      <MDBIcon icon="user-circle" size="2x" className="pink-text mr-3"/>
      
    <Icon size="2x" type="message" theme="twoTone" twoToneColor="#a71d31" />
        <Link  className="text-justify">&nbsp;Contact us &nbsp;</Link>
      </Breadcrumb.Item>
  </Breadcrumb>
  </MDBCardBody>
</MDBCard>
<br/>
 
    <MDBRow >
  <MDBCol md="12" className="mb-4">
    <MDBCard color="white" text="black" className="text-center" style={{maxWidth:""}}>
      <MDBCardBody>
{/*         
      <MDBIcon far icon="comment-alt" /><span style={{color:"red"}}>
   {this.state.dbMessage}
  </span> */}
        <Form layout={ori} onSubmit={this.handleSubmit} style={{maxWidth:"500px",marginLeft:"auto",marginRight:"auto"}}>

          <MDBRow >
<MDBCol md="12">
          <Form.Item label={"Full name "
        }>{getFieldDecorator('fname', {
          rules: [{ required: true, message: 'Please input your nickname!', whitespace: true }],
        })(<Input />)}
        </Form.Item>

<br />
     
      <Form.Item label="E-mail">
        {getFieldDecorator('email', {
          rules: [
            {
              type: 'email',
              message: 'The input is not valid E-mail!',
            },
            {
              required: true,
              message: 'Please input your E-mail!',
            },
          ],
        })(<Input />)}
<br /> 

      </Form.Item> <Form.Item label={
         "Second name "}>{getFieldDecorator('lname', {
          rules: [{ required: true, message: 'Please input your nickname!', whitespace: true }],
        })(<TextArea />)}
      </Form.Item>
      <Form.Item
      style={{marginLeft:"auto",marginRight:"auto"}}
      >
        <Button style={mButton} type="primary" htmlType="submit"> Send message
                </Button>
    
      </Form.Item> 
</MDBCol> 

   
      </MDBRow>
    </Form>
    </MDBCardBody>
        </MDBCard>
      </MDBCol>
    </MDBRow>
  </MDBCardBody>
 </MDBCard>

<br />

</div>
       );
            }}

const grid = Form.create({ name: 'time_related_controls' })(Shop1);
export default grid;
