import * as React from 'react';
import { MDBCard, MDBCardBody, MDBCardImage,MDBCardTitle,MDBCardText,MDBBtn,MDBCol} from "mdbreact";
import Texty from 'rc-texty';

const murl="http://lmgexhibitions.kilaqitu.com/img/";



class About extends React.Component {
 
 
    render() {

       
      
        return (<div style={{maxWidth:"",marginLeft:"auto",marginRight:"auto",paddingTop:"52px",}}>
  <MDBCard style={{ width:"95%",maxWidth: "1400px",marginRight: "auto",marginLeft: "auto",marginTop: "2px",}}>
 
 <MDBCardBody>
 <MDBCard color="white" text="black" className="text-center" >
      <MDBCardBody>
      <h4><Texty type="mask-bottom">Le Meilleur</Texty></h4>
  </MDBCardBody>
</MDBCard>
        </MDBCardBody>
      </MDBCard>

      <MDBCard style={{ width:"95%",maxWidth: "700px",marginRight: "auto",marginLeft: "auto",marginTop: "2px",}}>
 
 <MDBCardBody>
 <MDBCard color="white" text="black" className="text-center" >
      <MDBCardBody>
      <p>&nbsp;TOP Exhibitions & Marketing Services has been trading for many years now, becoming the supplier of choice for many exhibitions and Conferences in Kenya. We are well known in the industry and have an outstanding reputation for expertise in our field.  We have a wealth of experience and have worked in most of the big venues within Kenya.

</p>
<br/>
<p>
&nbsp;This enables us to assist organizers with the planning of their event from registration, floor plan designing, signage, exhibitions stands build both customised and regular shell scheme, furniture extras hire and security  perspective</p>
  </MDBCardBody>
</MDBCard>
        </MDBCardBody>
      </MDBCard>
</div>
        
        );
    }
}
export default About;
