    borderBottomRightRadius: 0
  },
  suggestionsContainer: {
    display: 'none'
  },
  suggestionsContainerOpen: {
    display: 'block',
    position: 'absolute',
    top: 51,
    width: "100%",
    maxHeight:"400px",
    overflow:"auto",
    fontFamily: 'Helvetica, sans-serif',
    border: '1px solid #aaa',
    backgroundColor: '#fff',
    // fontWeight: 300,
    // fontSize: 16,
    color:"black",
    alignContent: 'left',
    borderBottomLeftRadius: 4,
    borderBottomRightRadius: 4,
    zIndex: 2
  },
  suggestionsList: {
    margin: 0,
    padding: 0,
    listStyleType: 'none',
  },
  suggestion: {
    cursor: 'pointer',
    padding: '10px 20px'
  },
  suggestionHighlighted: {
    backgroundColor: '#ddd'
  }
};
const axios = require('axios');
function getMatchingLanguages(value) {
  const escapedValue = escapeRegexCharacters(value.trim());
  
  if (escapedValue === '') {
    return [];
  }
  
  const regex = new RegExp('^' + escapedValue, 'i');

  return languages.filter(language => regex.test(language.name));
}

/* ----------- */
/*    Utils    */
/* ----------- */

// https://developer.mozilla.org/en/docs/Web/JavaScript/Guide/Regular_Expressions#Using_Special_Characters
function escapeRegexCharacters(str) {
  return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

/* --------------- */
/*    Component    */
/* --------------- */

function getSuggestionValue(suggestion) {
  return suggestion.name;
}

function renderSuggestion(suggestion) {
  return (
    <span style={{textAlign:"left"}}>{suggestion.name}</span>
   
  );
}

class About1 extends React.Component {
  constructor() {
    super();

    this.state = {
      value: '',
      suggestions: [],
      isLoading: false,
      rs:"",
      info:"",
      url:"http://192.168.0.101/nw/kq/api.php?apicall="
      // url:"http://192.168.0.100/nw/kq/api.php?apicall="
      // url:"http://192.168.173.1/nw/kq/api.php?apicall="
      // url:"http://localhost/nw/kq/api.php?apicall=
    };
    
    this.lastRequestId = null;
  }
  

  fData=(filter)=> {
    var values="s="+filter;
    //  console.log("_4 received: "+f4);
     axios({
       method: 'post',
       url: this.state.url+'search5',
       data: values,
       config: { headers: {'Content-Type': 'multipart/form-data' }}
   })
   .then((response) => {
   
     if(response.data.error){
       console.log("_4err "+response.data.message);
       }else{
         var Result=response.data._p;
         if(typeof Result!="undefined"& Result!=null){ this.setState({
          suggestions:Result
              })
            }
     
        
       
       console.log("_4Result: "+JSON.stringify(Result)); 
       }
   })
   .catch(function (response) {
       console.log("_4error"+response)
   });
   }

