import * as React from 'react';
import Masonry from 'react-masonry-component';
import { MDBCard, MDBCardBody, MDBCardImage,MDBCardTitle,MDBCardText,MDBBtn,MDBCol} from "mdbreact";

import { enquireScreen } from 'enquire-js';
let isMobile;
let mbl1="";
const l1={ maxWidth:"1000px",marginLeft:"-12px",marginRight:"auto",marginTop:"72px",marginBottom: "22px"};
const l2={ maxWidth:"1000px",marginLeft:"-12px",marginRight:"auto",marginTop:"72px",marginBottom: "22px"};
enquireScreen((b) => {
  isMobile = b;
  if(isMobile){
    mbl1=l1
  }else{
    mbl1=l2
  }
 
});
 const mbl=mbl1;
const murl="http://lmgexhibitions.kilaqitu.com/img/";
const masonryOptions = {
    transitionDuration: 0
};

const imagesLoadedOptions = { background: '.my-bg-image-el' }
const theData = [
  
{
  img: murl+"p4.jpeg",
txt: 'Event Management',
t1:"At LMG we help to plan and organise exhibition events for your. Our team of experts are commited to ensure your event becomes successful."
},
{
  img: murl+"p3.jpeg",
  txt: 'Pavillion DesignBuild',
  t1:"At LMG you get the best designers and builders who ensure you get the best Pavillion designs and builds At LMG you get the best designers and builders who ensure you get the best Pavillion designs and builds"
},
{
  img: murl+"p7.jpeg",
  txt: 'Custom DesignStand',
  t1:"Want stands that blend with the brand attributes of your organization, then look no further cntuct us today."
},
{ img: murl+"p8.jpeg",
txt: 'Exhibition stands Hire',
t1:"There are cases where it might be more relevant to hire than have full customized builds. We are here to to provide solutions that meet all financial and time related needs."
},
{
    img: murl+"p1.jpeg",
    txt: 'Conference Backdrops & AV supply',
    t1:"We also provide conference backdrops and audio visal installations."
}
]
class Services extends React.Component {
 
 
    render() {
        const childElements = theData.map((e,i)=>{
           return (
                // <li className="image-element-class">
                //   {e.txt}
                //     <img src={e.img}/>
                // </li>
                
      <MDBCard style={{ width:"99%",maxWidth: "300px",marginRight: "12px",marginLeft: "12px",marginTop: "32px",}}>
 
        <img className="img-fluid" src={e.img}  />
        <MDBCardBody>
          <h5>{e.txt}</h5>
          <br/>
          <MDBCardText>
          {e.t1}
          </MDBCardText>
        </MDBCardBody>
      </MDBCard>
                    
            );
        });
      
        return (<div style={{maxWidth:"",marginLeft:"auto",marginRight:"auto",paddingTop:"52px",}}>
  <MDBCard style={{ width:"95%",maxWidth: "1400px",marginRight: "auto",marginLeft: "auto",marginTop: "2px",}}>
 
 <MDBCardBody>
 <MDBCard color="white" text="black" className="text-center" >
      <MDBCardBody>
      <h4>Products and services offered</h4>
  </MDBCardBody>
</MDBCard>
  
 {isMobile ? (      <Masonry
                className={'my-gallery-class'} // default ''
                elementType={'div'} // default 'div'
                options={masonryOptions} // default {}
                disableImagesLoaded={false} // default false
                updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
                imagesLoadedOptions={imagesLoadedOptions} // default {}
                
                style={{ maxWidth:"1000px",marginLeft:"-12px",marginRight:"auto",marginBottom: "22px"}} 
            >
                {childElements}
               
        </Masonry>
      ) : (      <Masonry
        className={'my-gallery-class'} // default ''
        elementType={'div'} // default 'div'
        options={masonryOptions} // default {}
        disableImagesLoaded={false} // default false
        updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
        imagesLoadedOptions={imagesLoadedOptions} // default {}
        
        style={{ maxWidth:"1000px",marginLeft:"auto",marginRight:"auto",marginBottom: "22px"}} 
    >
        {childElements}
       
</Masonry>
      )}


        </MDBCardBody>
      </MDBCard>
</div>
        
        );
    }
}
export default Services;
