import React from "react";
import { Form, Breadcrumb,Input, Button,Icon,Tooltip, } from 'antd';

import { Redirect } from 'react-router';
import { BrowserRouter as Router } from "react-router-dom";
import {MDBFooter, MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBIcon,} from 'mdbreact';

import { Route, Switch } from "react-router-dom";

import home from "./home";
import geo from "./geo";
import product from "./product";
// const { MonthPicker, RangePicker } = DatePicker;
const axios = require('axios');

class Shop1 extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
      redirect: false,
    };
  
   
    handleSubmit = e => {
      e.preventDefault();
  
      this.props.form.validateFields((err, fieldsValue) => {
        if (err) {
          return;
        }
        const values = {
          fieldsValue
        };
        console.log('Received values of form: ', values);
        axios({
          method: 'post',
          // url: 'http://localhost/nw/vap/regApi.php?apicall=signup'
          // url: 'http://localhost/nw/kq/api.php?apicall=signup',
          url: 'http://192.168.43.87/d/php/api.php?apicall=signup',
          data: values,
          config: { headers: {'Content-Type': 'multipart/form-data' }}
      })
      .then((response) => {

        if(response.data.error){
          console.log("error "+response.data.message);
          }else{
            this.setState({
              redirect: true
           });
          
          console.log("success "+response.data.message);
          }

      })
      .catch(function (response) {
          //handle error
          console.log("error"+response)
      });
      });
    };

  
    handleWebsiteChange = value => {
      let autoCompleteResult;
      if (!value) {
        autoCompleteResult = [];
      } else {
        autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
      }
      this.setState({ autoCompleteResult });
    };
    compareToFirstPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && value !== form.getFieldValue('password')) {
        callback('Two passwords that you enter is inconsistent!');
      } else {
        callback();
      }
    };
  
    validateToNextPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && this.state.confirmDirty) {
        form.validateFields(['confirm'], { force: true });
      }
      callback();
    };
    
render() {
  
  //const { getFieldDecorator } = this.props.form;
  const { getFieldDecorator,} = this.props.form;

  const { redirect  } = this.state;
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 8 },
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 16 },
    },
  };
 
  if (redirect) {
    return <Redirect to='/shop'/>;
  }
  return (
<div>
<Router>
        <div className="flyout" style={{backgroundColor:"#fff"}}>
    
         
       
        <div style={{ marginTop: "3.5rem" }}>
        <Breadcrumb>
 
    <Breadcrumb.Item to="/product">
      <Icon type="home" />
    </Breadcrumb.Item>
   
    <Breadcrumb.Item href="/"to="/product">
      <Icon type="user" />
      <span>Profile</span>
    </Breadcrumb.Item>
    <Breadcrumb.Item>Shop</Breadcrumb.Item>
  </Breadcrumb>
        <Switch>
        
        <Route exact path ="/"component={home}/>
       
        <Route exact path ="/geo"component={geo}/>
        <Route exact path ="/product"component={product}/>

    
        <Route
          render={function () {
            return <h1>Not Found</h1>;
          }}
        />
      </Switch>
          </div>
          <MDBFooter color="" style={{backgroundColor:"#213357",zIndex: "",}}>
            <p className="footer-copyright mb-0 py-3 text-center">
              &copy; {new Date().getFullYear()} Copyright:{" "}
              <a href="https://www.MDBootstrap.com"> cladi.com </a>
            </p>
          </MDBFooter>
        </div>
      </Router>
</div>
       );
            }}

const grid = Form.create({ name: 'time_related_controls' })(Shop1);
export default grid;
