import * as React from 'react';
import Masonry from 'react-masonry-component';
import { MDBCard, MDBCardBody, MDBCardImage,MDBCardTitle,MDBCardText,MDBBtn,MDBCol} from "mdbreact";

import Banner3 from './Home3/Banner3';
import {Banner30DataSource_a,} from './Home3/data.source';

import { enquireScreen } from 'enquire-js';
let isMobile;
enquireScreen((b) => {
  isMobile = b;
});
 
const murl="http://lmgexhibitions.kilaqitu.com/img/";
const masonryOptions = {
    transitionDuration: 0
};

const imagesLoadedOptions = { background: '.my-bg-image-el' }
const theData = [
  {
    img: murl+"ca.jpeg",
    txt: 'Conference Backdrops & AV supply',
    t1:"We also provide conference backdrops and audio visal installations."
}
,
{
  img: murl+"p.jpeg",
  txt: 'Pavillion Design Build',
  t1:"At LMG Exhibitions, you get the best designers and carpenters who are dedicated to ensure that your brand looks attractive from all ends.Our experienced team of designers and carpenters also help in advising on the best materials and trendy designs in the market in order to make your pavillion stand out at any event. "
},
{
  img: murl+"cs.jpeg",
  txt: 'Custom Design Stand',
  t1:"We do design and manufacture of stands using quality materials.We come up with latest and innovative designs that blend with the brand attributes of your organiztaion tailored to transform your ideas into reality."
},
{ img: murl+"es.jpeg",
txt: 'Exhibition stands Hire',
t1:"There are cases where it might be more relevant to hire than have full customized builds. We are here to to provide solutions that meet all financial and time related needs."
},
{
  img: murl+"em.jpeg",
txt: 'Event Management',
t1:"At LMG we help to plan and organise exhibition events for your. Our team of experts are commited to ensure your event becomes successful."
}
]
const eca = [
  {
    img: murl+"ca.jpeg",
    txt: 'Music',
    t1:"We also provide conference backdrops and audio visal installations."
}
,
{
  img: murl+"p.jpeg",
  txt: 'Karate',
  t1:"At LMG Exhibitions, you get the best designers and carpenters who are dedicated to ensure that your brand looks attractive from all ends.Our experienced team of designers and carpenters also help in advising on the best materials and trendy designs in the market in order to make your pavillion stand out at any event. "
},
{
  img: murl+"cs.jpeg",
  txt: 'Swimming',
  t1:"We do design and manufacture of stands using quality materials.We come up with latest and innovative designs that blend with the brand attributes of your organiztaion tailored to transform your ideas into reality."
},
{ img: murl+"es.jpeg",
txt: 'Arts',
t1:"There are cases where it might be more relevant to hire than have full customized builds. We are here to to provide solutions that meet all financial and time related needs."
}
]
class Services extends React.Component {
 
 
    render() {
        const childElements = theData.map((e,i)=>{
           return (
                // <li className="image-element-class">
                //   {e.txt}
                //     <img src={e.img}/>
                // </li>
                
      <MDBCard style={{ width:"99%",maxWidth: "300px",marginRight: "12px",marginLeft: "12px",marginTop: "32px",}}>
 
        <img className="img-fluid" style={{maxWidth: "287px",paddingLeft:"12px",paddingTop:"19px"}} src={e.img}  />
        <MDBCardBody>
          <h5>{e.txt}</h5>
          <br/>
          <MDBCardText>
          {e.t1}
          </MDBCardText>
        </MDBCardBody>
      </MDBCard>
                    
            );
        });
      
        const eca1 = eca.map((e,i)=>{
          return (
               // <li className="image-element-class">
               //   {e.txt}
               //     <img src={e.img}/>
               // </li>
               
     <MDBCard style={{ width:"99%",maxWidth: "300px",marginRight: "12px",marginLeft: "12px",marginTop: "32px",}}>

       <img className="img-fluid" style={{maxWidth: "287px",paddingLeft:"12px",paddingTop:"19px"}} src={e.img}  />
       <MDBCardBody>
         <h5>{e.txt}</h5>
         <br/>
         <MDBCardText>
         {e.t1}
         </MDBCardText>
       </MDBCardBody>
     </MDBCard>
                   
           );
       });
        return (

<div style={{maxWidth:"",marginLeft:"auto",marginRight:"auto",}}>
  
<Banner3
        id="Banner3_0"
        key="Banner3_0"
        dataSource={Banner30DataSource_a}
        isMobile={isMobile}
      />

<div className= 'home-page-wrapper content8-wrapper'>
  <div className='home-page content8'>
    <MDBCard style={{ width:"95%",maxWidth: "1400px",marginRight: "auto",marginLeft: "auto",marginTop: "2px",boxShadow:"0 32px 32px rgba(34, 94, 222, 0.08)"}} className="text-center">
      <MDBCardBody>
        <h4>Products and services offered</h4>
      {isMobile ? (      
        <Masonry
                className={'my-gallery-class'} // default ''
                elementType={'div'} // default 'div'
                options={masonryOptions} // default {}
                disableImagesLoaded={false} // default false
                updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
                imagesLoadedOptions={imagesLoadedOptions} // default {}
                
                style={{ maxWidth:"1000px",marginLeft:"-12px",marginRight:"auto",marginBottom: "22px"}} 
            >
                {childElements}
               
        </Masonry>
        ) : (      
        <Masonry
        className={'my-gallery-class'} // default ''
        elementType={'div'} // default 'div'
        options={masonryOptions} // default {}
        disableImagesLoaded={false} // default false
        updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
        imagesLoadedOptions={imagesLoadedOptions} // default {}
        
        style={{ maxWidth:"1000px",marginLeft:"auto",marginRight:"auto",marginBottom: "22px"}} 
        >
        {childElements}
       
        </Masonry>
      )}

    </MDBCardBody>
  </MDBCard>

  <MDBCard color="white lighten-1" style={{boxShadow:"0 32px 32px rgba(34, 94, 222, 0.08)",maxWidth:"700px",marginLeft:"auto",marginRight:"auto",marginTop:"72px"}} text="black" className="text-center">
    <MDBCardBody style={{color:"#697b8c"}}>
          <h5 style={{}}>Special Programs </h5>
          <p style={{whiteSpace:"pre-wrap",marginTop:"22px"}} >Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nam velit nulla, lacinia nec varius id, tempor sed dolor. Sed eget tincidunt lacus, eu tempus nibh. Praesent lacinia ex a magna varius porta. Morbi ultrices posuere varius. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia Curae; Donec eget justo semper magna venenatis sagittis sed sed lorem. Praesent lobortis nisi ac justo facilisis condimentum. Praesent felis dui, auctor sit amet lectus vitae, tincidunt auctor mi.</p>
        
        </MDBCardBody>
        </MDBCard>

        <MDBCard style={{ width:"95%",maxWidth: "1400px",marginRight: "auto",marginLeft: "auto",marginTop: "72px",boxShadow:"0 32px 32px rgba(34, 94, 222, 0.08)"}} className="text-center">
      <MDBCardBody>
        <h4>Extra curricular activities</h4>
      {isMobile ? (      
        <Masonry
                className={'my-gallery-class'} // default ''
                elementType={'div'} // default 'div'
                options={masonryOptions} // default {}
                disableImagesLoaded={false} // default false
                updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
                imagesLoadedOptions={imagesLoadedOptions} // default {}
                
                style={{ maxWidth:"1000px",marginLeft:"",marginRight:"auto",marginBottom: "22px"}} 
            >
                {eca1}
               
        </Masonry>
        ) : (      
        <Masonry
        className={'my-gallery-class'} // default ''
        elementType={'div'} // default 'div'
        options={masonryOptions} // default {}
        disableImagesLoaded={false} // default false
        updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
        imagesLoadedOptions={imagesLoadedOptions} // default {}
        
        style={{ maxWidth:"1000px",marginLeft:"auto",marginRight:"auto",marginBottom: "22px"}} 
        >
        {eca1}
       
        </Masonry>
      )}

    </MDBCardBody>
  </MDBCard>
</div></div>
</div>
        
        );
    }
}
export default Services;
