import React from "react";
import PropTypes from "prop-types";
import ReactInputPosition, {
  TOUCH_ACTIVATION,
  MOUSE_ACTIVATION
} from "react-input-position";
import SideBySideRenderer from "./SideBySideRenderer";

import { MDBContainer, MDBRow, MDBCol,MDBBtn } from "mdbreact";

const SideBySideMagnifier = props => {
  const {
    imageSrc,
    largeImageSrc,
    imageAlt,
    overlayOpacity,
    overlayBoxOpacity,
    cursorStyle,
    alwaysInPlace,
    transitionSpeed,
    transitionSpeedInPlace,
    renderOverlay,
    className,
    url,
    style
  } = props;
  url:this.props.location.state.url
  return (
      <MDBContainer className="mt-1">

<MDBRow>
    <MDBCol md="5">
      
    <ReactInputPosition
      style={style}
      className={className}
      touchActivationMethod={TOUCH_ACTIVATION.TOUCH}
      mouseActivationMethod={MOUSE_ACTIVATION.HOVER}
      trackItemPosition
      linkItemToActive
    >
       
      <SideBySideRenderer
      style={{ maxWidth:"100px",height:"100%",width:"100%" }}
        imageSrc={imageSrc}
        largeImageSrc={largeImageSrc}
        imageAlt={imageAlt}
        overlayOpacity={overlayOpacity}
        overlayBoxOpacity={overlayBoxOpacity}
        alwaysInPlace={alwaysInPlace}
        transitionSpeed={transitionSpeed}
        transitionSpeedInPlace={transitionSpeedInPlace}
        renderOverlay={renderOverlay}
        cursorStyle={cursorStyle}
      />
       </ReactInputPosition>
  
{/* 
        <img src={this.state.url} style={{ maxWidth:"100%",height:"100%",width:"100%" }}/> */}
    </MDBCol>
    <MDBCol md="7" style={{paddingTop: "30px",boxShadow:"4px 1px 9px 0px #99999929"}}>
<MDBContainer style={{boxShadow:"#c7c7c729 0px 14px 10px 0px",marginBottom:"40%"}}>
    <h3 className="text-center">
    Oversized Textured Top + {url}
   
    </h3>
    <br/>
    <p className="text-center">High collar top with short cuffed sleeves. Asymmetric hem with side slits</p>
    <br/>  <br/>
</MDBContainer>

<MDBContainer className="text-center">

<p> <b>Price</b> : KSH 259</p>
<br/>
</MDBContainer>
<MDBContainer>
<MDBRow>
    <MDBCol md="2">
 
    </MDBCol>
    <MDBCol md="8" >
    <MDBBtn color="white" style={{boxShadow:" 0 2px 0px 0 rgba(0, 0, 0, 0.16), 0 2px 2px 0 rgba(0, 0, 0, 0.12)",marginRight: "27%",}}>Add to bag</MDBBtn>
    <MDBBtn color="white" style={{boxShadow:" 0 2px 0px 0 rgba(0, 0, 0, 0.16), 0 2px 2px 0 rgba(0, 0, 0, 0.12)",}}>Buy now</MDBBtn>
    </MDBCol>
    <MDBCol md="2">
 
    </MDBCol>
</MDBRow>
</MDBContainer>
</MDBCol>
</MDBRow>
</MDBContainer>

    
  );
};

SideBySideMagnifier.propTypes = {
  imageSrc: PropTypes.string,
  largeImageSrc: PropTypes.string,
  imageAlt: PropTypes.string,
  overlayOpacity: PropTypes.number,
  overlayBoxOpacity: PropTypes.number,
  cursorStyle: PropTypes.string,
  alwaysInPlace: PropTypes.bool,
  transitionSpeed: PropTypes.number,
  transitionSpeedInPlace: PropTypes.number,
  renderOverlay: PropTypes.func,
  className: PropTypes.string,
  style: PropTypes.object
};

SideBySideMagnifier.defaultProps = {
  imageSrc: require("../assets/img/2.jpg"),
  largeImageSrc: "",
  imageAlt: "",
  overlayOpacity: 0.5,
  overlayBoxOpacity: 0.8,
  cursorStyle: "crosshair",
  transitionSpeed: 0.4,
  transitionSpeedInPlace: 0.4
};

export default SideBySideMagnifier;
