import * as React from 'react';
import Masonry from 'react-masonry-component';
import { MDBCard, MDBCardBody, MDBCardImage,MDBCardTitle,MDBCardText,MDBBtn,MDBCol} from "mdbreact";

import { enquireScreen } from 'enquire-js';
let isMobile;
enquireScreen((b) => {
  isMobile = b;
});
 
const murl="http://192.168.0.102/nw/img/";
const masonryOptions = {
    transitionDuration: 0
};

const imagesLoadedOptions = { background: '.my-bg-image-el' }
const theData = [
  {
    img: murl+"a.jpg",
    txt: 'Grilled salmon cubes with vegetables',
    t1:"We also provide conference backdrops and audio visal installations."
}
,
{
  img: murl+"b.jpg",
  txt: 'Pavillion Design Build',
  t1:"At LMG Exhibitions, you get the best designers and carpenters who are dedicated to ensure that your brand looks attractive from all ends.Our experienced team of designers "
},
{
  img: murl+"c.jpg",
  txt: 'Custom Design Stand',
  t1:"We do design and manufacture of stands using quality materials.We come up with latest and innovative designs that blend with the brand attributes of your organiztaio."
},
{ img: murl+"d.jpg",
txt: 'Exhibition stands Hire',
t1:"There are cases where it might be more relevant to hire than have full customized builds. We are here to to provide solutions that meet all financial and time related needs."
},
{
  img: murl+"e.jpg",
txt: 'Spicy papaya',
t1:"At LMG we help to plan and organise exhibition events for your. Our team of experts are commited to ensure your event becomes successful."
},
{
  img: murl+"f.jpg",
txt: 'thanksgiving-dinner',
t1:"At LMG we help to plan and organise exhibition events for your. Our team of experts are commited to ensure your event becomes successful."
}
]
class Services extends React.Component {
 
 
    render() {
        const childElements = theData.map((e,i)=>{
           return (
                // <li className="image-element-class">
                //   {e.txt}
                //     <img src={e.img}/>
                // </li>
                
      <MDBCard to={{pathname:"/more",
      state: { url:e.img,H:"Belted Plaid Blazer",d:"Flowy blazer with lapel collar and long sleeves. Self belt. Chest patch pockets and welt pockets at hip. Front double-breasted button closure.",C:"500"} }} 

      style={{ width:"99%",maxWidth: "400px",marginRight: "72px",marginLeft: "",marginTop: "32px",cursor:"pointer"}}>
 
        <img className="img-fluid" style={{maxWidth: "887px",width:"100%",}} src={e.img}  />
        <MDBCardBody>
          <h5>{e.txt}</h5>
          <br/>
          <MDBCardText>
          {e.t1}
          </MDBCardText>
        </MDBCardBody>
      </MDBCard>
                    
            );
        });
      
        return (<div style={{maxWidth:"",marginLeft:"auto",marginRight:"auto",paddingTop:"52px",}}>
    <MDBCard style={{ width:"95%",maxWidth: "1400px",marginRight: "auto",marginLeft: "auto",marginTop: "2px",boxShadow:"0 32px 32px rgba(34, 94, 222, 0.08)"}} className="text-center">
      <MDBCardBody>

      <h4>Dishes offered</h4>
  
  
 {isMobile ? (      <Masonry
                className={'my-gallery-class'} // default ''
                elementType={'div'} // default 'div'
                options={masonryOptions} // default {}
                disableImagesLoaded={false} // default false
                updateOnEachImageLoad={true} // default false and works only if disableImagesLoaded is false
                imagesLoadedOptions={imagesLoadedOptions} // default {}
                
                style={{ maxWidth:"1000px",marginLeft:"-12px",marginRight:"auto",marginBottom: "22px"}} 
            >
                {childElements}
               
        </Masonry>
      ) : (      <Masonry
        className={'my-gallery-class'} // default ''
        elementType={'div'} // default 'div'
        options={masonryOptions} // default {}
        disableImagesLoaded={false} // default false
        updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
        imagesLoadedOptions={imagesLoadedOptions} // default {}
        
        style={{ maxWidth:"1000px",marginLeft:"auto",marginRight:"auto",marginBottom: "22px"}} 
    >
        {childElements}
       
</Masonry>
      )}


        </MDBCardBody>
      </MDBCard>
</div>
        
        );
    }
}
export default Services;
