import React from "react";
import { Form, Breadcrumb,Input, Button,Icon,Tooltip,Select,AutoComplete, } from 'antd';

import { MDBCard, MDBCardBody, MDBCol, MDBRow} from "mdbreact";
import { Redirect } from 'react-router';

 const AutoCompleteOption = AutoComplete.Option;
// const { MonthPicker, RangePicker } = DatePicker;
const axios = require('axios');
const residences = [
  {
    value: 'zhejiang',
    label: 'Zhejiang',
    children: [
      {
        value: 'hangzhou',
        label: 'Hangzhou',
        children: [
          {
            value: 'xihu',
            label: 'West Lake',
          },
        ],
      },
    ],
  },
  {
    value: 'jiangsu',
    label: 'Jiangsu',
    children: [
      {
        value: 'nanjing',
        label: 'Nanjing',
        children: [
          {
            value: 'zhonghuamen',
            label: 'Zhong Hua Men',
          },
        ],
      },
    ],
  },
];

class Shop1 extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
      redirect: false,
    };
  
   
    handleSubmit = e => {
      e.preventDefault();
  
      this.props.form.validateFields((err, fieldsValue) => {
        if (err) {
          return;
        }
        const values = {
          fieldsValue
        };
        console.log('Received values of form: ', values);
        axios({
          method: 'post',
          // url: 'http://localhost/nw/vap/regApi.php?apicall=signup'
          // url: 'http://localhost/nw/kq/api.php?apicall=signup',
          url: 'http://192.168.43.87/d/php/api.php?apicall=signup',
          data: values,
          config: { headers: {'Content-Type': 'multipart/form-data' }}
      })
      .then((response) => {

        if(response.data.error){
          console.log("error "+response.data.message);
          }else{
            this.setState({
              redirect: true
           });
          
          console.log("success "+response.data.message);
          }

      })
      .catch(function (response) {
          //handle error
          console.log("error"+response)
      });
      });
    };

  
    handleWebsiteChange = value => {
      let autoCompleteResult;
      if (!value) {
        autoCompleteResult = [];
      } else {
        autoCompleteResult = ['.com', '.org', '.net'].map(domain => `${value}${domain}`);
      }
      this.setState({ autoCompleteResult });
    };
    compareToFirstPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && value !== form.getFieldValue('password')) {
        callback('Two passwords that you enter is inconsistent!');
      } else {
        callback();
      }
    };
  
    validateToNextPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && this.state.confirmDirty) {
        form.validateFields(['confirm'], { force: true });
      }
      callback();
    };
    
render() {
  
  //const { getFieldDecorator } = this.props.form;
  const { getFieldDecorator, getFieldsError, getFieldError, isFieldTouched } = this.props.form;

  const { redirect  } = this.state;
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 8 },
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 16 },
    },
  };
 
  if (redirect) {
    return <Redirect to='/shop'/>;
  }
  return (
<div>

<MDBCard color="white" text="black" className="text-center">
  <MDBCardBody>

  <Breadcrumb separator=">">
    <Breadcrumb.Item href="grid">Grid</Breadcrumb.Item>
    <Breadcrumb.Item href="about-us">about Center</Breadcrumb.Item>
    <Breadcrumb.Item href="geo">Geo List</Breadcrumb.Item>
    <Breadcrumb.Item>An Application</Breadcrumb.Item>
  </Breadcrumb>

    <MDBRow >
  
  <MDBCol md="12" className="mb-4">
    <MDBCard color="white" text="black" className="text-center" style={{maxWidth:""}}>
      <MDBCardBody>
        <Form {...formItemLayout} onSubmit={this.handleSubmit}>

          <MDBRow >
<MDBCol md="6">
          <Form.Item label={
          <span>First name
            <Tooltip title="Enter your first name">
              <Icon type="question-circle-o" />
            </Tooltip>
          </span>
        }>{getFieldDecorator('fname', {
          rules: [{ required: true, message: 'Please input your nickname!', whitespace: true }],
        })(<Input />)}
        </Form.Item>

<br />
      <Form.Item label={
          <span>Second name
            <Tooltip title="Enter your second name">
              <Icon type="question-circle-o" />
            </Tooltip>
          </span>
        }>{getFieldDecorator('lname', {
          rules: [{ required: true, message: 'Please input your nickname!', whitespace: true }],
        })(<Input />)}
      </Form.Item>
<br /> 
      <Form.Item label="E-mail">
        {getFieldDecorator('email', {
          rules: [
            {
              type: 'email',
              message: 'The input is not valid E-mail!',
            },
            {
              required: true,
              message: 'Please input your E-mail!',
            },
          ],
        })(<Input />)}
      </Form.Item>
</MDBCol> 
<MDBCol md="6">   

      <Form.Item label="Password" hasFeedback>
        {getFieldDecorator('password', {
          rules: [{ required: true, message: 'Please input your Password!' },
          {validator: this.validateToNextPassword,}],
        })(
          <Input
            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
            type="password"
            placeholder="Password"
          />,
        )}
      </Form.Item>

<br /> 
<Form.Item  label="Confirm Password" hasFeedback>
        {getFieldDecorator('confirm', {
          rules: [{ required: true, message: 'Please confirm your password!' },
          {validator: this.compareToFirstPassword,}],
        })(
          <Input
            prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
            type="password"
            placeholder="Password"
          />,
        )}
      </Form.Item>

<br />
      <Form.Item label={
        <span>Phone number
          <Tooltip title="Enter your phone number">
            <Icon type="question-circle-o" />
          </Tooltip>
        </span>}>{getFieldDecorator('pno', {
          rules: [{ required: true, message: 'Please input your Phone number!', whitespace: true }],
        })(<Input />)}
        </Form.Item>
<br />
</MDBCol>  
      <Form.Item
      style={{marginLeft:"auto",marginRight:"auto"}}
      >
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item> 
      </MDBRow>
    </Form>
    </MDBCardBody>
        </MDBCard>
      </MDBCol>
    </MDBRow>
  </MDBCardBody>
 </MDBCard>

<br />




</div>
       );
            }}

const grid = Form.create({ name: 'time_related_controls' })(Shop1);
export default grid;
