import React, { Component } from "react";
import {
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarNav,
  MDBNavbarToggler,
  MDBCollapse,
  MDBNavItem,
  MDBFooter,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBDropdown,
  MDBNavLink
} from "mdbreact";
import { ReactComponent as Logo } from "./assets/logo.svg";
import { BrowserRouter as Router } from "react-router-dom";
import Routes from "./Routes";

class App extends Component {
  state = {
    collapseID: ""

  };

  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));

  closeCollapse = collapseID => () => {
    window.scrollTo(0, 0);
    this.state.collapseID === collapseID && this.setState({ collapseID: "" });
  };

  // g=()=>{
  //  if(localStorage.getItem("url")!= undefined){
  //    if(localStorage.getItem("url")==0){
  //     localStorage.getItem("url")=1
  //      this.props.history.push("/f");
  //    }else{
  //     localStorage.getItem("url")=0
  //     this.props.history.push("/m");
  //    }
  //  }}
  render() {
    const overlay = (
      <div
        id="sidenav-overlay"
        style={{ backgroundColor: "transparent" }}
        onClick={this.toggleCollapse("mainNavbarCollapse")}
      />
    );

    const { collapseID } = this.state;

    return (
      <Router>
        <div className="flyout">
          <MDBNavbar style={{paddingTop:"1px",paddingBottom: "1px",}}color="white" dark expand="md" fixed="top" scrolling>
            <MDBNavbarBrand href="/" style={{color:"black",paddingTop:"5px",paddingBottom: "5px",}}>
              <img src={require('./assets/lg.png')} style={{ height: "", width: "3.7rem",marginBottom: "-22px",marginRight:"12px",boxShadow:"rgb(249, 247, 247) 0px 1px 0px 0px, rgb(253, 251, 251) 0px 3px 0px 0px",borderRadius:"50%" }}/>cladi

             {/* <Logo style={{ height: "2.5rem", width: "2.5rem" }} /> */}

            </MDBNavbarBrand>
            <MDBNavbarToggler
            className="black" 
              onClick={this.toggleCollapse("mainNavbarCollapse")}
            />
            <MDBCollapse
              id="mainNavbarCollapse"
              isOpen={this.state.collapseID}
              navbar
              className="dark-grey-text"
            >
              <MDBNavbarNav right>
                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    exact

                    className="dark-grey-text"
                  >
                  .
                  </MDBNavLink>
                  </MDBNavItem>

                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    exact
                    to={{pathname:"/",
                    }}
                    onClick={this.closeCollapse("mainNavbarCollapse")}
                    className="dark-grey-text"
                  >
                    Home
                  </MDBNavLink>
                </MDBNavItem>
                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    exact
                    to={{pathname:"/m",
                      state: {  state: { 
                      women:false,
                      men:true
                    }}
                  }}
                    onClick={this.closeCollapse("mainNavbarCollapse")}
                    className="dark-grey-text"
                  >
                    Men
                  </MDBNavLink>
                </MDBNavItem>
                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    exact
                    to={{pathname:"/f",
                    state: { 
                      women:true,
                      men:false
                    }
                    }}
                    onClick={this.closeCollapse("mainNavbarCollapse")}
                    className="dark-grey-text"
                  >
                    Women
                  </MDBNavLink>
                </MDBNavItem>

                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    onClick={this.closeCollapse("mainNavbarCollapse")}
                    to={{pathname:"/",

                    }}
                    className="dark-grey-text"
                  >
                    All Products
                  </MDBNavLink>
                </MDBNavItem>

                <MDBNavItem style={{paddingRight: "22px",}}>
                  <MDBNavLink
                    onClick={this.closeCollapse("mainNavbarCollapse")}
                    to="/forms"
                    className="dark-grey-text"
                  >
                    Log in
                  </MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>
                  <MDBNavLink
                  >

                  </MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>
                  <MDBNavLink
                  >
                    Modals
                  </MDBNavLink>
                </MDBNavItem>
                <MDBNavItem>
                  <MDBNavLink
                  >
                    Addons
                  </MDBNavLink>
                </MDBNavItem>
              </MDBNavbarNav>
            </MDBCollapse>
          </MDBNavbar>
          {collapseID && overlay}
          <main style={{ marginTop: "3.5rem" }}>
            <Routes />
          </main>
          <MDBFooter color="" style={{backgroundColor:"#213357",zIndex: "",}}>
            <p className="footer-copyright mb-0 py-3 text-center">
              &copy; {new Date().getFullYear()} Copyright:{" "}
              <a href="https://www.MDBootstrap.com"> cladi.com </a>
            </p>
          </MDBFooter>
        </div>
      </Router>
    );
  }
}

export default App;
