import React from "react";
import { Form, Input, Button,Breadcrumb,message} from 'antd';
import { MDBIcon,MDBCard, MDBCardBody, MDBCol, MDBRow} from "mdbreact";
import { Redirect } from 'react-router';
import ReactMapGL,{Marker,NavigationControl} from 'react-map-gl';
import {Link } from "react-router-dom";
import { timingSafeEqual } from "crypto";
import { throwStatement } from "@babel/types";


// const { MonthPicker, RangePicker } = DatePicker;
const axios = require('axios');


class Geo1 extends React.Component {
    state = {
      confirmDirty: false,
      autoCompleteResult: [],
      redirect: false,
      mDisplay:"none",
      required:true,
      mrp:"none",
      mrp1:"block",
       geo_options : {
        enableHighAccuracy: true, 
        maximumAge        : 0, 
        timeout           : 10000
      },
      viewport: {
        width: 400,
        height: 400,
        latitude: 1.255833599,
        longitude: 36.8566272,
        zoom: 8
      },
      userLocation:{}
    };
  componentDidMount(){
    if (typeof window.orientation !== 'undefined') { 
      message.config({
        top: 100,
        duration: 5,
        maxCount: 3,
      });
      message.success("Go oust side for high acuracy")
      this.setState({
        mrp:"block",
        mrp1:"none"
      })

     }else{
      message.config({
        top: 100,
        duration: 5,
        maxCount: 3,
      });
       message.error("Use a mobile device for high acuracy")
       this.setState({
         mrp:"none",
         mrp1:"block"
       })
     }
  }
   
    handleSubmit = e => {
      e.preventDefault();
  
      this.props.form.validateFields((err, fieldsValue) => {
        if (err) {
          return;
        }
        const values = {
          fieldsValue
        };
        console.log('Received values of form: ', values);
        axios({
          method: 'post',
          // url: 'http://localhost/nw/vap/regApi.php?apicall=signup'
          // url: 'http://localhost/nw/kq/api.php?apicall=signup',
          url: 'http://192.168.43.87/d/php/api.php?apicall=signup',
          data: values,
          config: { headers: {'Content-Type': 'multipart/form-data' }}
      })
      .then((response) => {

        if(response.data.error){
          console.log("error "+response.data.message);
          }else{
            this.setState({
              redirect: true
           });
          
          console.log("success "+response.data.message);
          }

      })
      .catch(function (response) {
          //handle error
          console.log("error"+response)
      });
      });
    };

  
  
    compareToFirstPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && value !== form.getFieldValue('password')) {
        callback('Two passwords that you enter is inconsistent!');
      } else {
        callback();
      }
    };
  
    validateToNextPassword = (rule, value, callback) => {
      const { form } = this.props;
      if (value && this.state.confirmDirty) {
        form.validateFields(['confirm'], { force: true });
      }
      callback();
    };
   
      setUserLocation = () => {

        navigator.geolocation.getCurrentPosition(position => {
            let setUserLocation = {
                lat: position.coords.latitude,
                long: position.coords.longitude
             };
            let newViewport = {
                height: 400,
                width: 400,
                latitude: position.coords.latitude,
                longitude: position.coords.longitude,
                zoom: 17
            }
            console.log("lat="+this.latitude);
            this.setState({
                viewport: newViewport,
                userLocation:setUserLocation,
                mDisplay:"block",
                required:false
            })
        })
    };
    
     getLocation=()=> {
      navigator.permissions.query({name:'geolocation'}).then(function(result) {
        message.error(result.state);
        if (result.state == 'granted') {
          message.info(result.state);
        }
      });
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(this.showPosition,this.state.geo_options);
        
      } else { 
       message.error("Geolocation is not supported by this browser.") ;
      }
    }
    
     showPosition=(position)=> {
       this.setUserLocation();
    }
    
     showError=(error)=> {
      message.config({
        top: 100,
        duration: 2.5,
        maxCount: 3,
      });
      switch(error.code) {
        case error.PERMISSION_DENIED:
          message.error( "User denied the request for Geolocation.");
          break;
        case error.POSITION_UNAVAILABLE:
          message.error( "Location information is unavailable.");
          break;
        case error.TIMEOUT:
          message.error( "The request to get user location timed out.");
          break;
        case error.UNKNOWN_ERROR:
          message.error( "An unknown error occurred.");
          break;
      }


       
    }
render() {
  
  //const { getFieldDecorator } = this.props.form;
  const { getFieldDecorator,  } = this.props.form;

  const { redirect  } = this.state;
  const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 8 },
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 16 },
    },
  };
 
  if (redirect) {
    return <Redirect to='/shop'/>;
  }
  return (
